  currentPageData: any;
  searchKey: string = '';
  routeListener = new Subscription();
  @Output() currentPageChange = new EventEmitter<any>();
  constructor(private route: Router, public sanitizer: DomSanitizer) {
    this.routeListener.add(this.route.events.subscribe((event: any) => {
      if (event instanceof NavigationEnd) {
        let route: any = event.url.split('/');
        route = route[route?.length - 1];
        this.getPageDatas(route);
      }
    }));
  }

  ngOnInit(): void {

  }

  getPageDatas(route: string): void {
    this.pageDatas.forEach((data: any) => {
      if (data?.routeName === route) this.currentPageData = data;
    });
    this.currentPageChange.emit(this.currentPageData);
  }

  searchText(event: any): void { }

  clearSearch(): void {
    this.searchKey = '';
  }

  ngOnDestroy(): void {
    this.routeListener.unsubscribe();
  }
